query ProjectIssues{
  repository(owner: "%PROJECT.github.repoOwner%", name: "%PROJECT.github.repository%") {
    projectsV2(first: 5) {
      nodes {
        id
        items(first: 100) {
          pageInfo{
            endCursor
            hasNextPage
          }
          nodes {
            content {
              ... on Issue {
                title
                bodyText
                updatedAt
                assignees(first:10){
                  nodes{
                    login
                  }
                }
                createdAt
                number
                linkedBranches(first:10){
                  totalCount
                  nodes{
                    ref{
                      name
                    }
                  }
                }
                closedByPullRequestsReferences(first:10){
                	totalCount
                  nodes{
                    state
                    author{
                      login
                    }
                    title
                  }
                }
                author {
                  login
                }
                issueType{
                  name
                }
              }
            }
            fieldValues(first: 100) {
              nodes {
                ... on ProjectV2ItemFieldUserValue {
                    field {
                        ... on ProjectV2Field {
                            name
                        }
                    }
                }
                ... on ProjectV2ItemFieldRepositoryValue {
                  field {
                    ... on ProjectV2Field {
                      name
                    }
                  }
                  repository {
                    nameWithOwner
                  }
                }
                ... on ProjectV2ItemFieldTextValue {
                  text
                  field {
                    ... on ProjectV2Field {
                      name
                    }
                  }
                }
                ... on ProjectV2ItemFieldMilestoneValue {
                    field {
                        ... on ProjectV2Field {
                            name
                        }
                    }
                    milestone {
                        number
                        title 
                    }
                }
                ... on ProjectV2ItemFieldSingleSelectValue {
                  name
                  updatedAt
                  creator {
                    login
                  }
                  field {
                    ... on ProjectV2SingleSelectField {
                      name
                    }
                  }
                }
              }
            }
          }
        }
      }
    }
  }
}

