query mergedPRs{
  repository(owner: "%PROJECT.github.repoOwner%", name: "%PROJECT.github.repository%") {
    pullRequests(first: 100, states: MERGED) {
      totalCount
      pageInfo{
        hasNextPage
        endCursor
      }
      nodes {
        id
        state
        title
        body
        baseRefName
        headRefName
        createdAt
        author {
          login
        }
        mergedAt
        mergedBy {
          login
        }
        reviews(first: 10) {
          totalCount
          nodes {
            createdAt
            state
          }
        }
      }
    }
  }
}

