module.exports.generic = function getReviews(inputData, variables) {
    let res = [];
    for (let pullRequest of inputData) {
        if (new Date(pullRequest.mergedAt) > new Date(variables.from) && new Date(pullRequest.mergedAt) < new Date(variables.to) && pullRequest.reviews.nodes.some(review => review.state === 'APPROVED')) {
            res.push(pullRequest);
        }
    }
    return res;
}

