module.exports.generic = function getPullRequestsWithAtLeastOneCommentByMember(inputData,variables)  {
    let pullRequestsMergedAndOpenWithAtLeastOneComment = []
    for (let pullRequest of inputData) {

        if (pullRequest.author.login === '%MEMBER.github.username%') continue;

        let isPullRequestAlreadyAdded = false;

        if (pullRequest.state === 'OPEN' || pullRequest.state === 'MERGED' && Math.max(new Date(variables.from), new Date(pullRequest.createdAt)) <= Math.min(new Date(variables.to), new Date(pullRequest.mergedAt))) {
            for (let comment of pullRequest.comments.nodes) {
                if (comment.author.login === '%MEMBER.github.username%' && new Date(comment.createdAt) > new Date(variables.from) && new Date(comment.createdAt) < new Date(variables.to)) {
                    pullRequestsMergedAndOpenWithAtLeastOneComment.push(pullRequest)
                    isPullRequestAlreadyAdded = true;
                    break;
                }
            }

            if (isPullRequestAlreadyAdded) continue;

            for (let review of pullRequest.reviews.nodes) {
                if (review.bodyText.length > 0 && review.author.login === '%MEMBER.github.username%' && new Date(review.createdAt) > new Date(variables.from) && new Date(review.createdAt) < new Date(variables.to)) {
                    pullRequestsMergedAndOpenWithAtLeastOneComment.push(pullRequest)
                    break;
                }
            }
        }
    }

    return pullRequestsMergedAndOpenWithAtLeastOneComment;
}

