{
  repository(owner: "%PROJECT.github.repoOwner%", name: "%PROJECT.github.repository%") {
    projectsV2(first: 5) {
      nodes {
        items(first: 100) {
          nodes {
            content {
              ... on Issue {
                title
                bodyText
                updatedAt
                assignees(first:10){
                  nodes{
                    login
                  }
                }
                createdAt
                number
                linkedBranches(first:10){
                  totalCount
                  nodes{
                    ref{
                      name
                    }
                  }
                }
                author {
                  login
                }
              }
            }
            fieldValues(first: 10) {
              nodes {
                ... on ProjectV2ItemFieldRepositoryValue {
                  field {
                    ... on ProjectV2Field {
                      name
                    }
                  }
                  repository {
                    nameWithOwner
                  }
                }
                ... on ProjectV2ItemFieldTextValue {
                  text
                  field {
                    ... on ProjectV2Field {
                      name
                    }
                  }
                }
                ... on ProjectV2ItemFieldSingleSelectValue {
                  name
                  updatedAt
                  creator {
                    login
                  }
                  field {
                    ... on ProjectV2SingleSelectField {
                      name
                    }
                  }
                }
              }
            }
          }
        }
      }
    }
  }
}

1.script:
module.exports.generic = function getInProgressIssues(inputData, variables) {
    inputData.data.repository.projectsV2.nodes.forEach(project => {
        project.items.nodes = project.items.nodes.filter(item => 
            item.fieldValues.nodes.some(fieldValue => 
                (fieldValue?.field?.name === "Status" && fieldValue?.name === "In Progress")
            )
        );
    });
    return inputData;
}

